select homepage_order.id, firstname, lastname, homepage_department.name, homepage_order.dateOrdered, homepage_order.customerPO, homepage_order.teamLab, homepage_order.description, homepage_jobstatus.name from homepage_order inner join homepage_person on homepage_order.customer_id = homepage_person.id inner join homepage_department on homepage_order.department_id = homepage_department.id inner join homepage_joborder on homepage_order.id = homepage_joborder.order_ptr_id inner join homepage_jobstatus on homepage_joborder.jobStatus_id = homepage_jobstatus.id where dateCompleted = 0 order by id asc